import React from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector,useDispatch } from 'react-redux';
import {getSingleMoviesRequest} from '../state/singleMovie'




const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
}));

const cards = [1];

export default function SingleMovie({match}) {
  const classes = useStyles();
  const dispatch = useDispatch()


const singleMovie = useSelector(state => state.singleMovies)

React.useEffect(()=>{
dispatch(getSingleMoviesRequest(match.params.id))
},[])


console.log(singleMovie)
  return (
    <React.Fragment >
      <CssBaseline />

      <main >

          <Grid container spacing={4} style ={{display:'flex',justifyContent:'center'}}>
            {cards.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card className={classes.card} style={{width:'500px',height:'600px'}}>
                  <CardMedia
                    className={classes.cardMedia}
                    image="https://source.unsplash.com/random"
                    title="Image title"
                    style={{height:'500px'}}

                  />
                  <CardContent className={classes.cardContent} style={{background:"black",color:'white'}}>
                    <Typography gutterBottom variant="h5" component="h2">
                      Heading
                    </Typography>
                    <Typography>
                      This is a media card. You can use this section to describe the content.
                    </Typography>
                  </CardContent>

                </Card>
              </Grid>
            ))}
          </Grid>

      </main>

    </React.Fragment>
  );
}
