import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import axios from 'axios'


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));





export default function SpacingGrid() {
  const [spacing, setSpacing] = React.useState(5);
  const classes = useStyles();
  const [movies,setMovies] = React.useState([])

  React.useEffect(()=>{
      /* let number = 510 */
      let page = 1
          axios
          .get(/* `https://api.themoviedb.org/3/movie/${number}?api_key=4620455e1c3ab4ab640f78e163013622` */ `https://api.themoviedb.org/3/movie/popular?api_key=4620455e1c3ab4ab640f78e163013622&language=en-US&page=${page}`)
          .then(res =>  {
               console.log(res.data.results)
              return setMovies(res.data.results)})
  },[])


  return (
      <React.Fragment>
    <Grid container className={classes.root} spacing={4} style = {{bakground :'grey'}}>
      <Grid item xs={12}>
        <Grid container justify="center" spacing={spacing}>
          {movies.map((movie) => (
             <Grid key={movie.id} item>
              <img className = {classes.paper}  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path }`} style = {{margin: '10% auto',width: '150px' , height: '210px'}} />
            </Grid>
          ))}
        </Grid>
      </Grid>
      <Grid item xs={12}>
        <Paper className={classes.control}>
          <Grid container>
            <Grid item>
              </Grid>
          </Grid>
        </Paper>
      </Grid>
    </Grid>
    </React.Fragment>
  );
}
