import React from 'react';
import { fade, makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { Link,Route } from 'react-router-dom';
import {getSingleMoviesRequest} from '../state/singleMovie'
import Movies from "../components/Movies"


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

export default function Header() {
  const classes = useStyles();

const enter = (e)=> {
  /* if(e.keyCode == '13'){ */
    const value = e.target.value
    return value
  /* } */}

  return (
    <div className={classes.root}>
      <AppBar position="static" style={{ background: '#2E3B55',margin:'0 auto', padding:'1em' }}>
        <Toolbar>

          <Typography variant="h6" className={classes.title} style ={{color:'white'}}>
          <Link to ='/'>
          <Button color="inherit" style ={{background : 'white'}}>OMDB</Button>
            </Link> 
          </Typography>

          <Link to={`/movies`}>

            <Button className={classes.search} style={{color:'white'}}> <SearchIcon /> </Button>

            </Link>


          <div className={classes.search} style={{margin:'margin'}}>

            <InputBase

               onKeyUp= {enter} 
              placeholder="Search…"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              inputProps={{ 'aria-label': 'search' }}
            />


          </div>
          <Link to = '/login'>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Log In
          </Button>
          </Link>

        </Toolbar>
      </AppBar>
      <div className={classes.offset} />
    </div>
  );
}
