import axios from "axios";
import { createReducer, createAsyncThunk } from "@reduxjs/toolkit";



export const getSingleMoviesRequest = createAsyncThunk("MOVIES", () => {
    /* let keycode = e.keyCode;
    if(keycode == '13'){ */
  return axios
    .get(
      `https://api.themoviedb.org/3/movie/124235?api_key=4620455e1c3ab4ab640f78e163013622&language=en-US`
    )
    .then((res) => res.data)
    .then((specificMovie) =>specificMovie.results) 
})


const singleMovieReducer = createReducer([], {
  [getSingleMoviesRequest.fulfilled]: (state, action) => action.payload,
});

export default singleMovieReducer;
