import React, { useEffect, useState } from "react";
import axios from "axios";


import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

import { log, success, error } from "../utils/logs";

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://www.elfinanciero.com.mx/uploads/2020/08/25/7695ea769f1598378727_standard_desktop_medium_retina.webp)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function User () {
  const classes = useStyles();
  const [image, setImage] = useState("");
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    log("secret attempt...");
    axios
      .get("/api/user")
      .then((res) => res.data)
      .then((img) => {
        setImage(img);
        setLoading(false);
        success(
          `Hello and, again, welcome to the Aperture Science computer-aided enrichment center.`
        );
      })
      .catch(({ response }) => {
        setLoading(false);
        error(response.status, response.statusText);
      });
  }, []);

  if (loading) return null;

  if (image) {
    return (
      <Grid container component="main" className={classes.root}>
        <CssBaseline />
        <Grid item xs={false} sm={4} md={7} className={classes.image} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <div className={classes.paper}>

            <Typography component="h1" variant="h5">
              Bienvenido Lionel, esta es tu casa
            </Typography>

            <Typography component="h1" variant="h5">
              Bienvenido Lionel, esta es tu casa
            </Typography>


          </div>
        </Grid>
      </Grid>
    );
  }

  return (
    <>
      <p className="text-red-600 text-6xl">404 Not Found</p>
    </>
  );
};
