import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import {useDispatch,useSelector} from 'react-redux'
import {getSearchMoviesRequest} from '../state/searchMovie'
import { Link} from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  },
  image: {
    width: 128,
    height: 128,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
}));






export default function SpacingGrid({match}) {
 console.log(match)
  const classes = useStyles();
let queryMovie = useSelector((state)=> state.searchMovie)
  queryMovie = queryMovie.results;

const dispatch = useDispatch();
  const movie = match.params.id

  React.useEffect(()=>{
    dispatch(getSearchMoviesRequest(movie))
  },[dispatch])



  return (

    <Grid container className={classes.root} >
    <h1 style={{color:'#2E3B55',fontFamily:'monospace', textAlign:'center', margin:'10px auto'}}>Populars Movies Selection of the week</h1>
    <Grid item xs={12} >
      <Grid container justify="center" spacing={3} >

        {queryMovie && queryMovie.map((movie) => (
           <Grid key={movie.id} item  >
             <Link to={`/movie/${movie.id}` } >
            <img className = {classes.paper} src={`https://image.tmdb.org/t/p/w500/${movie.poster_path }`} style = {{margin: '10% auto',width: '150px' , height: '210px'}} />
            </Link>

          </Grid>
        ))}



        </Grid>

    </Grid>

  </Grid>

  );

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} alt="complex" src="/static/images/grid/complex.jpg" />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
                <Typography gutterBottom variant="subtitle1">
                  Standard license
                </Typography>
                <Typography variant="body2" gutterBottom>
                  Full resolution 1920x1080 â€¢ JPEG
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  ID: 1030114
                </Typography>
              </Grid>
              <Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  Remove
                </Typography>
              </Grid>
            </Grid>
            <Grid item>
              <Typography variant="subtitle1">$19.00</Typography>
            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
}
