import axios from "axios";
import { createReducer, createAsyncThunk } from "@reduxjs/toolkit";



export const getSingleMoviesRequest = createAsyncThunk("MOVIES", (e,thunkAPI) => {
  return axios
    .get(
      `https://api.themoviedb.org/3/search/movie?api_key=4620455e1c3ab4ab640f78e163013622&query=${batman}`
    )
    .then((res) => res.data)
    .then((firstPage) =>firstPage.results) 
})


const singleMovieReducer = createReducer([], {
  [getSingleMoviesRequest.fulfilled]: (state, action) => action.payload,
});

export default singleMovieReducer;
