import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import axios from 'axios'
import { Link} from 'react-router-dom';
import {setMovies} from '../state/movies'
import {useSelector, useDispatch} from 'react-redux'


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));





export default function Body() {
  const movies = useSelector(state => state.movies);
  const dispatch = useDispatch();
  const classes = useStyles();
  /* let [movies,setMovies] = React.useState([]) */
  let popularMovies = [];

  React.useEffect(()=>{
          axios
          .get(`https://api.themoviedb.org/3/movie/popular?api_key=4620455e1c3ab4ab640f78e163013622&language=en-US&page=1`)
          .then(res => res.data)
          .then(firstPage =>  {
            popularMovies = firstPage.results
          return   axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=4620455e1c3ab4ab640f78e163013622&language=en-US&page=1`) 

            })
            .then(res => res.data)
            .then(secondPage=> {

              popularMovies = [...movies, ...secondPage.results]
              dispatch(setMovies(movies))
              setMovies(movies)

            })


  },[])


  return (

    <Grid container className={classes.root} >
      <Grid item xs={12} >
        <Grid container justify="center" spacing={3} >

          {movies.map((movie) => (
             <Grid key={movie.id} item>
               <Link to='/single-movie'>
              <img className = {classes.paper}  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path }`} style = {{margin: '10% auto',width: '150px' , height: '210px'}} />
              </Link>
            </Grid>
          ))}



          </Grid>

      </Grid>
    </Grid>

  );
}
