import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import axios from 'axios'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

export default function Body() {


const [movies,setMovies] = React.useState([])
const api_key = "20dac387"
React.useEffect(()=>{
        axios
        .get(`https://api.themoviedb.org/3/search/movie?api_key=${api_key}&query=batman`)
        .then(res =>  {
             console.log(res) 
            return setMovies([...movies, res])})
},[])


  const classes = useStyles();

  function FormRow() {

    return (
      <React.Fragment>
          { movies && movies.map(movie=> {
            console.log(movie)
          return <Grid item xs={4} key = {movie}>
          <Paper  className={classes.paper}><img src={movie.Poster} /></Paper>
          </Grid>})}


      </React.Fragment>
    );
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={1}>
        <Grid container item xs={12} spacing={3}>
          <FormRow />

        </Grid>
      </Grid>
    </div>
  );
}
