import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import axios from 'axios'


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    height: 140,
    width: 100,
  },
  control: {
    padding: theme.spacing(2),
  },
}));





export default function SpacingGrid() {
  const [spacing, setSpacing] = React.useState(5);
  const classes = useStyles();
  const [querymovies,setQuerymovies] = React.useState([])

  React.useEffect(()=>{

      let query = 'harry potter'
          axios
          .get(`https://api.themoviedb.org/3/search/movie?api_key=4620455e1c3ab4ab640f78e163013622&query=${query} `)
          .then(res =>  {
               console.log(res.data.results)
              return setQuerymovies(res.data.results)})
  },[])


  return (

    <Grid container className={classes.root} spacing={4} >
      <Grid item xs={12} >
        <Grid container justify="center" spacing={spacing} >
          {querymovies.map((movie) => (
             <Grid key={movie.id} >
              <img className = {classes.paper}  src={`https://image.tmdb.org/t/p/w500/${movie.poster_path }`} style = {{margin: '10% auto',width: '150px' , height: '210px'}} />
            </Grid>
          ))}



          </Grid>

      </Grid>
    </Grid>

  );
}
