import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import axios from 'axios'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },

}));

export default function Body() {


const [movies,setMovies] = React.useState([])

React.useEffect(()=>{
        axios
        .get("http://www.omdbapi.com/?apikey=20dac387&t=batman")
        .then(res =>  {
            /* console.log(res.data) */
            return setMovies([...movies, res.data])})
},[])


  const classes = useStyles();

  /* function FormRow() {

    return (
      <React.Fragment>
          { movies && movies.map(movie=> {
            console.log(movie)
          return <Grid item xs={4} key = {movie}>
           <img src={movie.Poster} />
          </Grid>})}


      </React.Fragment>
    );
  }

  return (
    <div className={classes.root}>
      <Grid container spacing={1}>
        <Grid container item xs={12} spacing={3}>
          <FormRow />

        </Grid>
      </Grid>
    </div>
  ); */


  return(

<div id="carouselExampleInterval" class="carousel slide" data-bs-ride="carousel">
  <div class="carousel-inner">
  { movies && movies.map(movie=> {
    return <div class="carousel-item" data-bs-interval="2000">
      <img src={movie.Poster} class="d-block w-100" alt="..." />
    </div> })}

  </div>
  <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleInterval"  data-bs-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Previous</span>
  </button>
  <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleInterval"  data-bs-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="visually-hidden">Next</span>
  </button>
</div> 
  )
}
